import React from 'react';

export default function TaskList({ task, onToggleDone, onDelete, onMarkDue }) {
  return (
    <div className="task">
      <div style={{ maxWidth: '70%' }}>
        <strong>{task.title}</strong>
        {task.description && <div className="muted">{task.description}</div>}
        <div className="muted small">Created: {new Date(task.createdAt).toLocaleString()}</div>
        {task.doneAt && <div className="muted small">Done: {new Date(task.doneAt).toLocaleString()}</div>}
      </div>
      <div className="actions">
        {task.status !== 'done' && <button onClick={onToggleDone}>Mark Done</button>}
        {task.status === 'done' && <button onClick={onToggleDone} className="ghost">Mark New</button>}
        {task.status !== 'due' && <button onClick={onMarkDue} className="ghost">Mark Due</button>}
        <button onClick={onDelete} className="ghost">Delete</button>
      </div>
    </div>
  );
}
