import React from "react";

export default function TaskList({ tasks, onDone }) {
  return (
    <div style={{ marginTop: 20 }}>
      {tasks.length === 0 && <p>No tasks yet.</p>}
      {tasks.map(task => (
        <div key={task._id} style={{ marginBottom: 10, border: "1px solid #ccc", padding: 10 }}>
          <h3 style={{ textDecoration: task.status === "done" ? "line-through" : "none" }}>
            {task.title}
          </h3>
          {task.description && <p>{task.description}</p>}
          {task.status !== "done" && (
            <button onClick={() => onDone(task._id)}>Mark Done</button>
          )}
        </div>
      ))}
    </div>
  );
}
