import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AddTaskForm from './components/AddTaskForm';
import TaskList from './components/TaskList';

const API = axios.create({ baseURL: process.env.REACT_APP_API_URL || 'http://localhost:5000/api' });

export default function App() {
  const [newTasks, setNewTasks] = useState([]);
  const [doneTasks, setDoneTasks] = useState([]);
  const [dueTasks, setDueTasks] = useState([]);
  const [loading, setLoading] = useState(false);

  const loadAll = async () => {
    setLoading(true);
    try {
      const [n, d, due] = await Promise.all([
        API.get('/tasks', { params: { status: 'new' } }),
        API.get('/tasks', { params: { status: 'done' } }),
        API.get('/tasks', { params: { status: 'due' } })
      ]);
      setNewTasks(n.data);
      setDoneTasks(d.data);
      setDueTasks(due.data);
    } catch (err) {
      console.error(err);
      alert('Failed to load tasks');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => { loadAll(); }, []);

  const handleAdd = async (payload) => {
    await API.post('/tasks', payload);
    await loadAll();
  };

  const handleToggleDone = async (task) => {
    if (task.status !== 'done') {
      await API.put(`/tasks/${task._id}`, { action: 'mark_done' });
    } else {
      await API.put(`/tasks/${task._id}`, { action: 'mark_new' });
    }
    await loadAll();
  };

  const handleMarkDue = async (task) => {
    await API.put(`/tasks/${task._id}`, { action: 'mark_due' });
    await loadAll();
  };

  const handleDelete = async (task) => {
    if (!window.confirm('Delete this task?')) return;
    await API.delete(`/tasks/${task._id}`);
    await loadAll();
  };

  const runPromotion = async () => {
    await API.post('/cron/promote-done');
    await loadAll();
  };

  return (
    <div className="container">
      <h1>Todo Fullstack</h1>
      <div className="card">
        <AddTaskForm onAdd={handleAdd} />
        <div style={{ marginTop: 8 }}>
          <button onClick={runPromotion} className="ghost">Run promotion (dev)</button>
        </div>
      </div>

      <div className="columns">
        <div className="column">
          <h3>New Tasks</h3>
          {loading ? <div className="muted">Loading...</div> : newTasks.length === 0 ? <div className="muted">No new tasks</div> : newTasks.map(t => (
            <TaskList key={t._id} task={t} onToggleDone={() => handleToggleDone(t)} onDelete={() => handleDelete(t)} onMarkDue={() => handleMarkDue(t)} />
          ))}
        </div>

        <div className="column">
          <h3>Previous Done Tasks</h3>
          {loading ? <div className="muted">Loading...</div> : doneTasks.length === 0 ? <div className="muted">No done tasks</div> : doneTasks.map(t => (
            <TaskList key={t._id} task={t} onToggleDone={() => handleToggleDone(t)} onDelete={() => handleDelete(t)} onMarkDue={() => handleMarkDue(t)} />
          ))}
        </div>

        <div className="column">
          <h3>Due Tasks</h3>
          {loading ? <div className="muted">Loading...</div> : dueTasks.length === 0 ? <div className="muted">No due tasks</div> : dueTasks.map(t => (
            <TaskList key={t._id} task={t} onToggleDone={() => handleToggleDone(t)} onDelete={() => handleDelete(t)} onMarkDue={() => handleMarkDue(t)} />
          ))}
        </div>
      </div>
    </div>
  );
}
